mapper.select:
SELECT id , name FROM sys_org WHERE status = 1 AND id != 1
AND name = #{name}
AND id = #{id}
order by last_update_time desc

SELECT su.id , su.user_name name, su.real_name real_name FROM sys_user su LEFT JOIN sys_user_role sur on su.id = sur.user_id LEFT JOIN sys_role sr on sur.role_id = sr.id LEFT JOIN sys_org so on sr.org_id = so.id WHERE su.status = 1 AND sur.status = 1 AND sr.status = 1 AND so.status = 1 AND so.id = #{orgId}
AND su.user_name = #{name}
AND su.real_name = #{realName}
AND su.id = #{id}
GROUP BY su.id , su.user_name order by su.last_update_time desc

SELECT id , name name, parent_id parent_id FROM sys_permission WHERE status = 1
AND name = #{name}
AND id = #{id}
order by last_update_time desc

SELECT sr.id , sr.name name FROM sys_role sr WHERE sr.status = 1
AND sr.name = #{name}
AND sr.id = #{id}
order by sr.last_update_time desc

SELECT sd.id , sd.name name FROM sys_department sd WHERE sd.status = 1
AND sd.name = #{name}
AND sd.id = #{id}
order by sd.last_update_time desc

SELECT scd.code , scd.value FROM sys_code sc LEFT JOIN sys_code_detail scd ON sc.id = scd.sys_code_id WHERE sc.type = #{type}
AND scd.code = #{code}
AND scd.value = #{value}
AND sc.status = 1 AND scd.status = 1 order by scd.last_update_time desc
