mapper.insert:
insert into tailor (order_no, tailoring_group, answer_cut_quantity, 
  actual_cut_quantity, monovalent, consuming_time, 
  tailoring_status, remarks, create_time, 
  create_user, last_update_time, last_update_user
  )
values (#{orderNo,jdbcType=VARCHAR}, #{tailoringGroup,jdbcType=VARCHAR}, #{answerCutQuantity,jdbcType=INTEGER}, 
  #{actualCutQuantity,jdbcType=INTEGER}, #{monovalent,jdbcType=DECIMAL}, #{consumingTime,jdbcType=DECIMAL}, 
  #{tailoringStatus,jdbcType=TINYINT}, #{remarks,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUser,jdbcType=VARCHAR}, #{lastUpdateTime,jdbcType=TIMESTAMP}, #{lastUpdateUser,jdbcType=VARCHAR}
  )
