mapper.select:
select

  distinct


from tailoring




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

mapper.insert:
insert into tailoring (order_id, tailoring_group, answer_cut_quantity, 
  actual_cut_quantity, monovalent, consuming_time, 
  `status`, remarks, create_time, 
  create_user, last_update_time, last_update_user
  )
values (#{orderId,jdbcType=BIGINT}, #{tailoringGroup,jdbcType=VARCHAR}, #{answerCutQuantity,jdbcType=INTEGER}, 
  #{actualCutQuantity,jdbcType=INTEGER}, #{monovalent,jdbcType=DECIMAL}, #{consumingTime,jdbcType=DECIMAL}, 
  #{status,jdbcType=TINYINT}, #{remarks,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUser,jdbcType=VARCHAR}, #{lastUpdateTime,jdbcType=TIMESTAMP}, #{lastUpdateUser,jdbcType=VARCHAR}
  )
